import React from 'react';

const AttendanceStatus = ({ student, attendance, onAttendanceChange }) => {
  return (
    <div>
      <p>{student.name}</p>
      <input
        type="checkbox"
        checked={attendance}
        onChange={(e) => onAttendanceChange(e.target.checked)}
      />
    </div>
  );
};

export default AttendanceStatus;
